// RSS Feed Debug and Processing
console.log('=== RSS FEED DEBUG START ===');

const allInputItems = $input.all();
console.log(`🔍 Total items received: ${allInputItems.length}`);

// Debug each item in detail
allInputItems.forEach((item, index) => {
  console.log(`📰 Item ${index + 1}:`);
  console.log(`  - Title: ${item.json.title || 'NO TITLE'}`);
  console.log(`  - Link: ${item.json.link || 'NO LINK'}`);
  console.log(`  - PubDate: ${item.json.pubDate || 'NO DATE'}`);
  console.log(`  - Description length: ${(item.json.description || '').length}`);
  console.log(`  - Content snippet length: ${(item.json.contentSnippet || '').length}`);
  console.log(`  - Creator: ${item.json.creator || 'NO CREATOR'}`);
  console.log(`  - Available keys: ${Object.keys(item.json).join(', ')}`);

  // Show first 100 chars of description
  if (item.json.description) {
    console.log(`  - Description preview: ${item.json.description.substring(0, 100)}...`);
  }
});

if (allInputItems.length === 0) {
  console.log('❌ ERROR: No RSS items received!');
  console.log('🔧 Possible causes:');
  console.log('  - RSS feed URL is blocked or unreachable');
  console.log('  - Network connectivity issues');
  console.log('  - RSS feed temporarily down');
  console.log('  - n8n timeout (default is usually 5-10 seconds)');

  return [{
    json: {
      error: 'No RSS items received',
      debug: 'RSS feeds may be failing or blocked',
      timestamp: new Date().toISOString(),
      suggestions: [
        'Check RSS URL in browser',
        'Verify network connectivity',
        'Check n8n timeout settings',
        'Try different RSS feed URL'
      ]
    }
  }];
}

// Process and return first few items for detailed inspection
const results = allInputItems.slice(0, 5).map((item, index) => {
  // Extract and clean description
  const description = item.json.description || item.json.contentSnippet || '';
  const cleanDescription = description.replace(/<[^>]*>/g, '').trim();

  return {
    json: {
      id: index + 1,
      title: (item.json.title || 'No Title').trim(),
      url: item.json.link || 'No URL',
      source: 'HubSpot',
      publishedAt: item.json.pubDate || 'No Date',
      summary: cleanDescription.substring(0, 200),
      creator: item.json.creator || 'Unknown',
      hasDescription: !!description,
      descriptionLength: description.length,
      cleanDescriptionLength: cleanDescription.length,
      allAvailableKeys: Object.keys(item.json),
      rawItemSample: {
        title: item.json.title,
        link: item.json.link,
        pubDate: item.json.pubDate,
        hasContent: !!(item.json.content || item.json.contentSnippet)
      }
    }
  };
});

console.log(`✅ Successfully processed ${results.length} items for analysis`);
console.log(`📊 Title lengths: ${results.map(r => r.json.title.length).join(', ')}`);
console.log(`🔗 All have URLs: ${results.every(r => r.json.url && r.json.url !== 'No URL')}`);
console.log(`📅 All have dates: ${results.every(r => r.json.publishedAt && r.json.publishedAt !== 'No Date')}`);
console.log('=== RSS FEED DEBUG END ===');

return results;
