// Process Articles for Strategic Trend Analysis

const TZ = 'Europe/London';
const MIN_TITLE_LEN = 10;
const MIN_SUMMARY_LEN = 30;
const MAX_ITEMS = 100;
const LOOKBACK_DAYS = 7;

const BRAND_TAGS = ['retail','sportswear','energy drinks'];
const REGION = 'UK';
const LIMITS = { max_items: 20, min_confidence: 40 };

function stripHtml(s='') {
  return (s || '')
    .replace(/<script[\s\S]*?<\/script>/gi, '')
    .replace(/<style[\s\S]*?<\/style>/gi, '')
    .replace(/<[^>]+>/g, '')
    .replace(/\s+/g, ' ')
    .trim();
}
function hostFromUrl(u='') { try { return new URL(u).hostname.replace(/^www\./,''); } catch { return ''; } }
function toISO(d) {
  if (!d) return null;
  const tryDates = Array.isArray(d) ? d : [d];
  for (const raw of tryDates) {
    const t = raw ? Date.parse(raw) : NaN;
    if (!Number.isNaN(t)) return new Date(t).toISOString();
  }
  return null;
}
function withinLookback(iso) {
  if (!iso) return false;
  const now = new Date();
  const then = new Date(now); then.setDate(now.getDate() - LOOKBACK_DAYS);
  return new Date(iso) >= then;
}
function normTitle(s='') { return s.toLowerCase().replace(/\s+/g,' ').trim(); }

const inputItems = $input.all();

// Flatten RSS outputs
let raw = [];
for (const it of inputItems) {
  if (Array.isArray(it.json?.items)) raw = raw.concat(it.json.items);
  else raw.push(it.json);
}

// Normalize articles for trend analysis
const normalised = [];
for (const r of raw) {
  const title = (r.title || r.headline || '').trim();
  const url = (r.link || r.url || r.guid || '').trim();
  const summary = stripHtml(r.description || r.contentSnippet || r.summary || r.content || '');
  const publishedAt = toISO([r.isoDate, r.pubDate, r.published, r.updated, r.date]) || new Date().toISOString();

  // Simplified source detection for trend context
  let source = 'Market Intelligence';
  const host = hostFromUrl(url);
  if (host.includes('adweek')) source = 'Adweek';
  else if (host.includes('campaignlive')) source = 'Campaign';
  else if (host.includes('reddit.com')) source = 'Reddit';

  if (title.length < MIN_TITLE_LEN) continue;
  if (summary.length < MIN_SUMMARY_LEN) continue;
  if (!withinLookback(publishedAt)) continue;

  normalised.push({ title, summary, source, publishedAt });
}

// Dedupe by title similarity
const seenTitles = new Set(); const deduped = [];
for (const it of normalised) {
  const key = normTitle(it.title);
  if (seenTitles.has(key)) continue;
  seenTitles.add(key);
  deduped.push(it);
}

// Sort and limit
deduped.sort((a,b) => new Date(b.publishedAt) - new Date(a.publishedAt));
const items = deduped.slice(0, MAX_ITEMS);

console.log(`üìä Processing ${items.length} articles for strategic trend extraction`);
console.log(`üéØ Target: Extract ${LIMITS.max_items} strategic trends for advertising agencies`);

// Create analysis summary for agent
const articleSummary = items.map(item => `"${item.title}" - ${item.summary.substring(0, 200)}...`).join('\n\n');

return [{
  json: {
    articleSummary,
    articleCount: items.length,
    sources: [...new Set(items.map(i => i.source))],
    brand_tags: BRAND_TAGS,
    region: REGION,
    limits: LIMITS
  }
}];

// Finalize strategic trends for visualization

const data = $json;
const trends = Array.isArray(data.trends) ? data.trends : [];

// Ensure minimum trend count
if (trends.length < 10) {
  throw new Error(`Only ${trends.length} trends generated. Minimum required: 10`);
}

// Fix sourceSummary structure
if (!data.sourceSummary || !Array.isArray(data.sourceSummary.sources)) {
  data.sourceSummary = {
    totalFetched: trends.length,
    afterCluster: trends.length,
    sources: ['Strategic Market Intelligence']
  };
}

// Enhanced viz properties for strategic trends
for (let i = 0; i < trends.length; i++) {
  const trend = trends[i];

  if (trend.scores) {
    const totalScore = trend.scores.total || 50;
    const velocity = trend.scores.velocity || 50;
    const novelty = trend.scores.novelty || 50;

    // Enhanced size mapping (10-20 for strategic importance)
    trend.viz = trend.viz || {};
    trend.viz.size = Math.max(10, Math.min(20, Math.round(10 + (totalScore / 100) * 10)));

    // Enhanced intensity based on velocity + novelty
    const intensityScore = (velocity + novelty) / 2;
    trend.viz.intensity = Number((1.2 + (intensityScore / 100) * 1.8).toFixed(2));

    // Strategic category colors
    const cat = (trend.category || 'Technology').toLowerCase();
    let hue;
    switch(cat) {
      case 'technology': case 'ai': hue = 240; break; // Blue
      case 'media': case 'culture': hue = 300; break; // Purple  
      case 'retail': case 'consumer behaviour': hue = 120; break; // Green
      case 'creativity': hue = 60; break; // Yellow
      case 'regulation': case 'data & privacy': hue = 0; break; // Red
      case 'sustainability': hue = 150; break; // Teal
      default: hue = 210; // Default blue
    }
    trend.viz.colorHint = `hsl(${hue}, 80%, 50%)`;

    // Add unique trend ID if missing
    if (!trend.id) {
      trend.id = `trend_${i + 1}_${Date.now().toString().slice(-4)}`;
    }
  }
}

data.generatedAt = data.generatedAt || new Date().toISOString();

// Strategic logging
console.log(`üéØ Generated ${trends.length} strategic trends for agency intelligence`);
const scoreRange = trends.map(t => t.scores?.total || 0);
const minScore = Math.min(...scoreRange);
const maxScore = Math.max(...scoreRange);
console.log(`üìä Strategic Impact Range: ${minScore}-${maxScore}`);
console.log(`üîç Categories: ${[...new Set(trends.map(t => t.category))].join(', ')}`);

trends.slice(0, 5).forEach((trend, i) => {
  console.log(`${i+1}. "${trend.title}" (${trend.category}) - Impact: ${trend.scores?.total}`);
});

return [{ json: data }];

// Generate 3D-style images for each trend using OpenAI's image API
// This node should be inserted between "Finalize Trends" and "Update Live Dashboard"

const OPENAI_API_KEY = $env.OPENAI_API_KEY;
const OPENAI_API_URL = 'https://api.openai.com/v1/images/generations';

// Image generation configuration
const IMAGE_CONFIG = {
  model: 'dall-e-3',
  size: '1024x1024',
  quality: 'standard',
  style: 'vivid',
  format: 'png'
};

// Generate image prompt for a trend
function generateImagePrompt(trend) {
  const { title, category, creative } = trend;

  // Use existing imagePrompt if available, otherwise generate one
  let basePrompt = creative?.imagePrompt || `A 3D visualization representing "${title}" in the ${category} category`;

  // Enhanced prompt template based on imageprompt.md requirements
  const enhancedPrompt = `Create a hyper-detailed 3D render of ${basePrompt}.

  ‚Ä¢ Perspective: Slightly angled from above, showing depth and dimension.
  ‚Ä¢ Primary color tone: Use colors that complement the ${category} theme prominently in lighting, accents, and key elements.
  ‚Ä¢ Details: Include realistic textures, materials, and lighting that represent the trend concept.
  ‚Ä¢ Symbolism: Incorporate visual metaphors that clearly represent "${title}" and its significance in ${category}.
  ‚Ä¢ Lighting: Studio-quality lighting with soft shadows and realistic reflections.
  ‚Ä¢ Style: Realistic yet slightly stylized for visual clarity, cinematic depth of field.
  ‚Ä¢ Background: Transparent PNG with alpha channel (no sky or scenery).
  ‚Ä¢ Format: 1024x1024 PNG with transparent background.`;

  return enhancedPrompt;
}

// Generate image for a single trend
async function generateTrendImage(trend) {
  try {
    const prompt = generateImagePrompt(trend);

    console.log(`üé® Generating image for: "${trend.title}"`);
    console.log(`üìù Prompt: ${prompt.substring(0, 100)}...`);

    const response = await fetch(OPENAI_API_URL, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${OPENAI_API_KEY}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        model: IMAGE_CONFIG.model,
        prompt: prompt,
        size: IMAGE_CONFIG.size,
        quality: IMAGE_CONFIG.quality,
        style: IMAGE_CONFIG.style,
        response_format: 'url'
      })
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(`OpenAI API error: ${errorData.error?.message || response.statusText}`);
    }

    const data = await response.json();
    const imageUrl = data.data[0].url;

    console.log(`‚úÖ Image generated successfully: ${imageUrl}`);

    return {
      ...trend,
      creative: {
        ...trend.creative,
        imageUrl: imageUrl
      }
    };

  } catch (error) {
    console.error(`‚ùå Failed to generate image for "${trend.title}":`, error.message);

    // Return trend without image URL on failure
    return {
      ...trend,
      creative: {
        ...trend.creative,
        imageUrl: null
      }
    };
  }
}

// Main execution
async function processTrends() {
  const inputData = $json;
  const trends = Array.isArray(inputData.trends) ? inputData.trends : [];

  if (trends.length === 0) {
    console.log('‚ö†Ô∏è No trends to process for image generation');
    return [{ json: inputData }];
  }

  console.log(`üñºÔ∏è Starting image generation for ${trends.length} trends...`);

  // Process trends sequentially to avoid rate limits
  const processedTrends = [];
  for (let i = 0; i < trends.length; i++) {
    const trend = trends[i];
    console.log(`üìä Processing trend ${i + 1}/${trends.length}: "${trend.title}"`);

    const processedTrend = await generateTrendImage(trend);
    processedTrends.push(processedTrend);

    // Rate limiting: wait 1 second between requests
    if (i < trends.length - 1) {
      await new Promise(resolve => setTimeout(resolve, 1000));
    }
  }

  // Count successful generations
  const successfulGenerations = processedTrends.filter(t => t.creative.imageUrl).length;
  console.log(`üéØ Image generation complete: ${successfulGenerations}/${trends.length} successful`);

  return [{
    json: {
      ...inputData,
      trends: processedTrends
    }
  }];
}

// Execute the main function
return await processTrends();
