// Configuration for RSS Data Sources
// Easy to update: just modify this array
const RSS_SOURCES = [
  {
    name: "campaignlive-news",
    url: "https://www.campaignlive.co.uk/rss/news",
    category: "Marketing News"
  },
  {
    name: "campaignlive-latest",
    url: "https://www.campaignlive.co.uk/rss/latest",
    category: "Marketing News"
  },
  {
    name: "adweek",
    url: "https://www.adweek.com/feed/",
    category: "Advertising News"
  },
  {
    name: "reddit-advertising",
    url: "https://www.reddit.com/r/advertising/.rss",
    category: "Community Discussion"
  },
  {
    name: "reddit-marketing",
    url: "https://www.reddit.com/r/marketing/.rss",
    category: "Community Discussion"
  }
];

// Workflow settings
const SETTINGS = {
  maxArticles: 100,
  minTitleLength: 10,
  minSummaryLength: 30,
  lookbackDays: 7,
  testingMode: false, // Set to false for production
  testingLimit: 10,   // Number of trends to process in production
  brandTags: ['retail', 'sportswear', 'energy drinks'],
  region: 'UK'
};

console.log(`üìä Configured ${RSS_SOURCES.length} RSS sources`);
console.log(`‚öôÔ∏è Testing mode: ${SETTINGS.testingMode} (limit: ${SETTINGS.testingLimit} trends)`);

return [{
  json: {
    rssSources: RSS_SOURCES,
    settings: SETTINGS,
    generatedAt: new Date().toISOString()
  }
}];

// Process Articles for Strategic Trend Analysis
// Uses settings from config node

const settings = $('Data Sources Config').first().json.settings;
const { maxArticles, minTitleLength, minSummaryLength, lookbackDays, brandTags, region } = settings;

function stripHtml(s='') {
  return (s || '')
    .replace(/<script[\s\S]*?<\/script>/gi, '')
    .replace(/<style[\s\S]*?<\/style>/gi, '')
    .replace(/<[^>]+>/g, '')
    .replace(/\s+/g, ' ')
    .trim();
}

function hostFromUrl(u='') { 
  try { 
    return new URL(u).hostname.replace(/^www\./,''); 
  } catch { 
    return ''; 
  } 
}

function toISO(d) {
  if (!d) return null;
  const tryDates = Array.isArray(d) ? d : [d];
  for (const raw of tryDates) {
    const t = raw ? Date.parse(raw) : NaN;
    if (!Number.isNaN(t)) return new Date(t).toISOString();
  }
  return null;
}

function withinLookback(iso) {
  if (!iso) return false;
  const now = new Date();
  const then = new Date(now); 
  then.setDate(now.getDate() - lookbackDays);
  return new Date(iso) >= then;
}

function normTitle(s='') { 
  return s.toLowerCase().replace(/\s+/g,' ').trim(); 
}

const inputItems = $input.all();

// Flatten RSS outputs
let raw = [];
for (const it of inputItems) {
  if (Array.isArray(it.json?.items)) {
    raw = raw.concat(it.json.items);
  } else {
    raw.push(it.json);
  }
}

// Normalize articles for trend analysis
const normalised = [];
for (const r of raw) {
  const title = (r.title || r.headline || '').trim();
  const url = (r.link || r.url || r.guid || '').trim();
  const summary = stripHtml(r.description || r.contentSnippet || r.summary || r.content || '');
  const publishedAt = toISO([r.isoDate, r.pubDate, r.published, r.updated, r.date]) || new Date().toISOString();

  // Simplified source detection for trend context
  let source = 'Market Intelligence';
  const host = hostFromUrl(url);
  if (host.includes('adweek')) source = 'Adweek';
  else if (host.includes('campaignlive')) source = 'Campaign';
  else if (host.includes('reddit.com')) source = 'Reddit';

  if (title.length < minTitleLength) continue;
  if (summary.length < minSummaryLength) continue;
  if (!withinLookback(publishedAt)) continue;

  normalised.push({ title, summary, source, publishedAt });
}

// Dedupe by title similarity
const seenTitles = new Set(); 
const deduped = [];
for (const it of normalised) {
  const key = normTitle(it.title);
  if (seenTitles.has(key)) continue;
  seenTitles.add(key);
  deduped.push(it);
}

// Sort and limit
deduped.sort((a,b) => new Date(b.publishedAt) - new Date(a.publishedAt));
const items = deduped.slice(0, maxArticles);

console.log(`üìä Processing ${items.length} articles for strategic trend extraction`);
console.log(`üéØ Target: Extract strategic trends for advertising agencies`);

// Create analysis summary for agent
const articleSummary = items.map(item => 
  `"${item.title}" - ${item.summary.substring(0, 200)}...`
).join('\n\n');

return [{
  json: {
    articleSummary,
    articleCount: items.length,
    sources: [...new Set(items.map(i => i.source))],
    brand_tags: brandTags,
    region: region,
    settings: settings
  }
}];

// Split articles into individual items for trend generation
// This enables individual trend processing

const inputData = $json;
const articles = inputData.articleSummary.split('\n\n');
const settings = inputData.settings;

console.log(`üîÑ Splitting ${articles.length} articles into individual trend generation items...`);

// Apply testing limit if enabled
const articlesToProcess = settings.testingMode 
  ? articles.slice(0, settings.testingLimit) 
  : articles;

console.log(`üìä Processing ${articlesToProcess.length} articles ${settings.testingMode ? '(TESTING MODE)' : '(FULL PRODUCTION)'}`);

// Create individual items for trend generation
const individualItems = articlesToProcess.map((article, index) => ({
  json: {
    articleSummary: article,
    articleCount: 1,
    sources: inputData.sources,
    brand_tags: inputData.brand_tags,
    region: inputData.region,
    settings: settings,
    trendIndex: index
  }
}));

console.log(`‚úÖ Created ${individualItems.length} individual trend generation items`);

return individualItems;

// Process single trend and prepare for image generation

const inputData = $json;
const trendData = inputData.choices?.[0]?.message?.content || inputData;

// Try to parse JSON if it's a string
let trend;
try {
  if (typeof trendData === 'string') {
    // Extract JSON from markdown code blocks if present
    const jsonMatch = trendData.match(/```json\s*([\s\S]*?)\s*```/);
    if (jsonMatch) {
      trend = JSON.parse(jsonMatch[1]);
    } else {
      trend = JSON.parse(trendData);
    }
  } else {
    trend = trendData;
  }
} catch (error) {
  console.error('‚ùå Failed to parse trend JSON:', error);
  console.error('Raw trend data:', trendData);
  throw new Error(`Failed to parse trend: ${error.message}`);
}

// Extract the single trend
const singleTrend = Array.isArray(trend.trends) ? trend.trends[0] : trend;

if (!singleTrend) {
  throw new Error('No trend found in response');
}

// Add unique ID if missing
if (!singleTrend.id) {
  singleTrend.id = $('Loop Over Trends').context.currentRunIndex || 0;
}

// Calculate viz properties
const totalScore = singleTrend.scores?.total || 50;
const velocity = singleTrend.scores?.velocity || 50;
const novelty = singleTrend.scores?.novelty || 50;
singleTrend.viz = singleTrend.viz || {};
singleTrend.viz.size = Math.max(10, Math.min(20, Math.round(10 + (totalScore / 100) * 10)));

const intensityScore = (velocity + novelty) / 2;
singleTrend.viz.intensity = Number((1.2 + (intensityScore / 100) * 1.8).toFixed(2));

// Strategic category colors
const cat = (singleTrend.category || 'Technology').toLowerCase();
let hue;
switch(cat) {
  case 'technology': case 'ai': hue = 240; break; // Blue
  case 'media': case 'culture': hue = 300; break; // Purple  
  case 'retail': case 'consumer behaviour': hue = 120; break; // Green
  case 'creativity': hue = 60; break; // Yellow
  case 'regulation': case 'data & privacy': hue = 0; break; // Red
  case 'sustainability': hue = 150; break; // Teal
  default: hue = 210; // Default blue
}
singleTrend.viz.colorHint = `hsl(${hue}, 80%, 50%)`;

console.log(`‚úÖ Generated trend: "${singleTrend.title}" (${singleTrend.category}) - Score: ${totalScore}`);

return [{
  json: {
    trend: singleTrend,
    generatedAt: new Date().toISOString()
  }
}];

// Generate image prompt for single trend

const trend = $json.trend;

// Use the existing imagePrompt as the base, or create one from the title
const basePrompt = trend.creative?.imagePrompt || `a 3D wireframe floating island`;

// Create the detailed prompt
const imagePrompt = `Create a hyper-detailed, high quality render of ${basePrompt} representing "${trend.title}" in the ${trend.category} category.

Style:
‚Ä¢ Clean 3D wireframe with thin, glowing lines and geometric shapes
‚Ä¢ Symbolism: Include visual elements symbolizing the trend theme "${trend.title}" and its significance in ${trend.category}
‚Ä¢ Materials: Transparent surfaces with visible wireframe edges
‚Ä¢ Primary color tone: Use ${trend.viz.colorHint} prominently in lighting, accents, and key elements
‚Ä¢ Lighting: Subtle glow along the wireframe lines with ${trend.viz.colorHint} color
‚Ä¢ Background: Transparent PNG with alpha channel (no sky or scenery)
‚Ä¢ Format: 1024x1024 PNG with transparent background`;

console.log(`üìù Generated image prompt for "${trend.title}": ${imagePrompt.substring(0, 100)}...`);

return [{
  json: {
    trend: trend,
    imagePrompt: imagePrompt,
    generatedAt: new Date().toISOString()
  }
}];

// Prepare API data for single trend

const trend = $json.trend;
const imageBinary = $json.base64Image;

console.log(`üì§ Preparing API data for trend: "${trend.title}"`);

// Create API payload
const apiData = {
  trends: [{
    ...trend,
    imageBinary: imageBinary
  }]
};

console.log(`‚úÖ Prepared API data with ${apiData.trends.length} trend(s)`);

return [{
  json: {
    apiData: apiData,
    trendTitle: trend.title,
    generatedAt: new Date().toISOString()
  }
}];

// Process API response and prepare for loop continuation

const apiResponse = $json;
const trendTitle = $('Prepare API Data').first().json.trendTitle;

console.log(`üìä API Response for "${trendTitle}":`, apiResponse);

// Check if API call was successful
if (apiResponse.status === 200 || apiResponse.statusCode === 200) {
  console.log(`‚úÖ Successfully sent "${trendTitle}" to API`);
} else {
  console.log(`‚ùå Failed to send "${trendTitle}" to API:`, apiResponse);
}

// Return data for loop continuation
return [{
  json: {
    success: true,
    trendTitle: trendTitle,
    apiResponse: apiResponse,
    processedAt: new Date().toISOString()
  }
}];

// Final summary when all trends are processed

const inputItems = $input.all();
const settings = $('Data Sources Config').first().json.settings;

console.log(`üéâ Workflow completed! Processed ${inputItems.length} trends`);

// Collect results
const results = inputItems.map(item => ({
  trendTitle: item.json.trendTitle,
  success: item.json.success,
  processedAt: item.json.processedAt
}));

const successfulTrends = results.filter(r => r.success).length;
const failedTrends = results.filter(r => !r.success).length;

console.log(`üìä Summary:`);
console.log(`  ‚úÖ Successful: ${successfulTrends}`);
console.log(`  ‚ùå Failed: ${failedTrends}`);
console.log(`  üìù Total: ${results.length}`);

// Log individual results
results.forEach((result, index) => {
  const status = result.success ? '‚úÖ' : '‚ùå';
  console.log(`  ${index + 1}. ${status} ${result.trendTitle}`);
});

return [{
  json: {
    summary: {
      totalTrends: results.length,
      successfulTrends: successfulTrends,
      failedTrends: failedTrends,
      settings: settings,
      completedAt: new Date().toISOString()
    },
    results: results
  }
}];

parameters.jsonSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "type": "object",
  "required": ["trends", "generatedAt"],
  "properties": {
    "generatedAt": { 
      "type": "string",
      "description": "ISO timestamp when trends were generated"
    },
    "trends": {
      "type": "array",
      "minItems": 1,
      "maxItems": 1,
      "description": "Array of strategic trends",
      "items": {
        "type": "object",
        "required": ["id", "title", "summary", "category", "scores", "whyItMatters", "tags", "brandAngles", "exampleUseCases", "creative", "viz"],
        "properties": {
          "id": { 
            "type": "string",
            "description": "Unique trend identifier"
          },
          "title": { 
            "type": "string",
            "description": "Strategic trend title",
            "minLength": 10
          },
          "summary": { 
            "type": "string",
            "description": "Brief trend summary",
            "minLength": 20
          },
          "category": { 
            "type": "string",
            "enum": ["Technology", "Media", "Culture", "Retail", "AI", "Consumer Behaviour", "Creativity", "Regulation", "Data & Privacy", "Sustainability"],
            "description": "Trend category"
          },
          "tags": { 
            "type": "array", 
            "items": { "type": "string" },
            "description": "Related tags"
          },
          "scores": {
            "type": "object",
            "required": ["novelty", "velocity", "relevance", "confidence", "total"],
            "properties": {
              "novelty": { 
                "type": "integer", 
                "minimum": 1, 
                "maximum": 100,
                "description": "How new is this trend"
              },
              "velocity": { 
                "type": "integer", 
                "minimum": 1, 
                "maximum": 100,
                "description": "How fast is this trend gaining momentum"
              },
              "relevance": { 
                "type": "integer", 
                "minimum": 1, 
                "maximum": 100,
                "description": "How much will this impact advertising agencies"
              },
              "confidence": { 
                "type": "integer", 
                "minimum": 1, 
                "maximum": 100,
                "description": "How certain are we this trend is real"
              },
              "total": { 
                "type": "integer", 
                "minimum": 1, 
                "maximum": 100,
                "description": "Weighted average score"
              }
            }
          },
          "whyItMatters": { 
            "type": "string",
            "description": "Why this trend matters for agencies",
            "minLength": 20
          },
          "brandAngles": { 
            "type": "array", 
            "items": { "type": "string" },
            "description": "Brand opportunities from this trend"
          },
          "exampleUseCases": { 
            "type": "array", 
            "items": { "type": "string" },
            "description": "Example use cases for agencies"
          },
          "creative": {
            "type": "object",
            "required": ["shortCardCopy", "imagePrompt", "altText", "podcastSnippet"],
            "properties": {
              "shortCardCopy": { 
                "type": "string", 
                "maxLength": 140,
                "description": "Short copy for cards"
              },
              "imagePrompt": { 
                "type": "string",
                "description": "Prompt for generating trend image"
              },
              "altText": { 
                "type": "string",
                "description": "Alt text for trend image"
              },
              "podcastSnippet": { 
                "type": "string",
                "description": "Snippet for podcast content"
              }
            }
          },
          "viz": {
            "type": "object",
            "required": ["size", "intensity", "colorHint"],
            "properties": {
              "size": { 
                "type": "number",
                "minimum": 1,
                "maximum": 20,
                "description": "Visual size for 3D rendering"
              },
              "intensity": { 
                "type": "number",
                "minimum": 0.5,
                "maximum": 3.0,
                "description": "Animation intensity"
              },
              "colorHint": { 
                "type": "string",
                "description": "CSS color hint for category"
              }
            }
          }
        }
      }
    }
  }
}
