// Finalize strategic trends for visualization

const data = $json;
const trends = Array.isArray(data.trends) ? data.trends : [];

// Ensure minimum trend count
if (trends.length < 10) {
  throw new Error(`Only ${trends.length} trends generated. Minimum required: 10`);
}

// Fix sourceSummary structure
if (!data.sourceSummary || !Array.isArray(data.sourceSummary.sources)) {
  data.sourceSummary = {
    totalFetched: trends.length,
    afterCluster: trends.length,
    sources: ['Strategic Market Intelligence']
  };
}

// Enhanced viz properties for strategic trends
for (let i = 0; i < trends.length; i++) {
  const trend = trends[i];

  if (trend.scores) {
    const totalScore = trend.scores.total || 50;
    const velocity = trend.scores.velocity || 50;
    const novelty = trend.scores.novelty || 50;

    // Enhanced size mapping (10-20 for strategic importance)
    trend.viz = trend.viz || {};
    trend.viz.size = Math.max(10, Math.min(20, Math.round(10 + (totalScore / 100) * 10)));

    // Enhanced intensity based on velocity + novelty
    const intensityScore = (velocity + novelty) / 2;
    trend.viz.intensity = Number((1.2 + (intensityScore / 100) * 1.8).toFixed(2));

    // Strategic category colors
    const cat = (trend.category || 'Technology').toLowerCase();
    let hue;
    switch(cat) {
      case 'technology': case 'ai': hue = 240; break; // Blue
      case 'media': case 'culture': hue = 300; break; // Purple  
      case 'retail': case 'consumer behaviour': hue = 120; break; // Green
      case 'creativity': hue = 60; break; // Yellow
      case 'regulation': case 'data & privacy': hue = 0; break; // Red
      case 'sustainability': hue = 150; break; // Teal
      default: hue = 210; // Default blue
    }
    trend.viz.colorHint = `hsl(${hue}, 80%, 50%)`;

    // Add unique trend ID if missing - use 0-based indexing for matching
    if (!trend.id) {
      trend.id = i; // Use 0-based indexing to match pairedItem.item
    } else {
      // Convert existing string ID to number if needed
      trend.id = parseInt(trend.id) || i;
    }
  }
}

data.generatedAt = data.generatedAt || new Date().toISOString();

// Strategic logging
console.log(`�� Generated ${trends.length} strategic trends for agency intelligence`);
const scoreRange = trends.map(t => t.scores?.total || 0);
const minScore = Math.min(...scoreRange);
const maxScore = Math.max(...scoreRange);
console.log(`📊 Strategic Impact Range: ${minScore}-${maxScore}`);
console.log(`�� Categories: ${[...new Set(trends.map(t => t.category))].join(', ')}`);

trends.slice(0, 5).forEach((trend, i) => {
  console.log(`${i+1}. "${trend.title}" (${trend.category}) - Impact: ${trend.scores?.total}`);
});

return [{ json: data }];

// Generate detailed image prompts for each trend using the template from imageprompt.md
// This node should be inserted between "Finalize Trends" and the OpenAI image generation node

const inputData = $json;
const trends = Array.isArray(inputData.trends) ? inputData.trends : [];

console.log(`🎨 Generating image prompts for ${trends.length} trends...`);

// ===== TESTING CONTROLS =====
// 🧪 FOR TESTING: Set to true to only process 2 trends (faster)
const TESTING_MODE = true;
const TESTING_LIMIT = 2;

// 🚀 FOR PRODUCTION: Set to false to process all trends
// const TESTING_MODE = false;

// Apply testing limit if enabled
const trendsToProcess = TESTING_MODE 
  ? trends.slice(0, TESTING_LIMIT) 
  : trends;

console.log(`�� Processing ${trendsToProcess.length} trends ${TESTING_MODE ? '(TESTING MODE)' : '(FULL PRODUCTION)'}`);

// Process each trend to create detailed image prompts
const processedTrends = trendsToProcess.map((trend, index) => {
  const { title, category, creative, viz } = trend;

  // Use the existing imagePrompt as the base, or create one from the title
  const basePrompt = creative?.imagePrompt || `a 3D wireframe floating island`;

  // Get the category color (use existing viz.colorHint or generate from category)
  const getCategoryColor = (cat) => {
    const categoryLower = (cat || 'Technology').toLowerCase();
    switch(categoryLower) {
      case 'technology': case 'ai': return '#3b82f6'; // Blue
      case 'media': case 'culture': return '#8b5cf6'; // Purple  
      case 'retail': case 'consumer behaviour': return '#10b981'; // Green
      case 'creativity': return '#f59e0b'; // Yellow/Orange
      case 'regulation': case 'data & privacy': return '#ef4444'; // Red
      case 'sustainability': return '#06b6d4'; // Teal
      default: return '#3b82f6'; // Default blue
    }
  };

  const categoryColor = viz?.colorHint || getCategoryColor(category);

  // Create the detailed prompt using the template from imageprompt.md
  const detailedPrompt = `Create a hyper-detailed, high quality render of ${basePrompt} representing "${title}" in the ${category} category.

Style:
• Clean 3D wireframe with thin, glowing lines and geometric shapes
• Symbolism: Include visual elements symbolizing the trend theme "${title}" and its significance in ${category}
• Materials: Transparent surfaces with visible wireframe edges
• Primary color tone: Use ${categoryColor} (hex color) prominently in lighting, accents, and key elements
• Lighting: Subtle glow along the wireframe lines with ${categoryColor} color
• Background: Transparent PNG with alpha channel (no sky or scenery)
• Format: 1024x1024 PNG with transparent background`;

  console.log(`📝 Generated prompt for "${title}": ${detailedPrompt.substring(0, 100)}...`);

  return {
    ...trend,
    imageGenerationPrompt: detailedPrompt
  };
});

console.log(`✅ Generated ${processedTrends.length} image prompts`);

return [{
  json: {
    ...inputData,
    trends: processedTrends
  }
}];

// Split trends into individual items for image generation
// This node creates separate items for each trend so the LangChain OpenAI node can process them individually

const inputData = $json;
const trends = Array.isArray(inputData.trends) ? inputData.trends : [];

console.log(`🔄 Splitting ${trends.length} trends into individual items for image generation...`);

// Process ALL trends for images
const trendsToProcess = trends;
console.log(`📊 Processing all ${trendsToProcess.length} trends for image generation`);

// Create separate items for each trend to process
// For LangChain OpenAI node, we need to structure the data differently
const individualTrends = trendsToProcess.map(trend => ({
  json: {
    trend: {
      ...trend,
      imageGenerationPrompt: trend.imageGenerationPrompt
    },
    trendId: trend.id,
    trendTitle: trend.title
  }
}));

console.log(`✅ Created ${individualTrends.length} individual trend items for image generation`);

return individualTrends;

// Process image files and convert to base64 for direct storage
const inputItems = $input.all();

console.log(`🖼️ Processing ${inputItems.length} items for base64 conversion...`);

const imageData = [];
inputItems.forEach((item, index) => {
  console.log(`\n�� Processing item ${index + 1}:`);
  console.log(`Item keys:`, Object.keys(item || {}));

  if (item.binary && item.binary.data) {
    // Convert binary data to base64
    const base64 = btoa(String.fromCharCode(...new Uint8Array(item.binary.data)));
    imageData.push({
      index: index,
      base64: base64,
      mimeType: 'image/png',
      size: item.binary.data.byteLength
    });
    console.log(`✅ Converted item ${index + 1} to base64 (${item.binary.data.byteLength} bytes)`);
  } else {
    console.log(`❌ No binary data found for item ${index + 1}`);
    imageData.push(null);
  }
});

console.log(`\n📊 Converted ${imageData.filter(d => d).length} images to base64`);

return [{
  json: {
    imageData: imageData,
    successfulConversions: imageData.filter(d => d).length,
    totalItems: inputItems.length,
    generatedAt: new Date().toISOString()
  }
}];

// Debug node to see exactly what the LangChain OpenAI node returns
// This will help us understand the data structure

const inputItems = $input.all();

console.log(`🔍 Debug: Processing ${inputItems.length} items from OpenAI node`);

// Log each item in detail
inputItems.forEach((item, index) => {
  console.log(`\n📋 Item ${index + 1}:`);
  console.log(`Type: ${typeof item.json}`);
  console.log(`Keys: ${Object.keys(item.json || {}).join(', ')}`);
  console.log(`Full JSON:`, JSON.stringify(item.json, null, 2));

  // Check if it's a string that might be a URL
  if (typeof item.json === 'string') {
    console.log(`String content: ${item.json.substring(0, 100)}...`);
    if (item.json.startsWith('http')) {
      console.log(`✅ Found URL: ${item.json}`);
    }
  }

  // Check nested structures
  if (item.json && typeof item.json === 'object') {
    if (item.json.data) {
      console.log(`Data object:`, JSON.stringify(item.json.data, null, 2));
    }
    if (item.json.result) {
      console.log(`Result:`, item.json.result);
    }
    if (item.json.url) {
      console.log(`URL:`, item.json.url);
    }
  }
});

// Try to extract any URLs we can find
const foundUrls = [];
inputItems.forEach((item, index) => {
  let url = null;

  // Check all possible locations
  if (typeof item.json === 'string' && item.json.startsWith('http')) {
    url = item.json;
  } else if (item.json && item.json.url) {
    url = item.json.url;
  } else if (item.json && item.json.data && item.json.data.url) {
    url = item.json.data.url;
  } else if (item.json && item.json.result) {
    url = item.json.result;
  } else if (item.json && item.json.imageUrl) {
    url = item.json.imageUrl;
  }

  if (url) {
    console.log(`✅ Found URL for item ${index + 1}: ${url}`);
    foundUrls.push(url);
  } else {
    console.log(`❌ No URL found for item ${index + 1}`);
    foundUrls.push(null);
  }
});

console.log(`\n📊 Summary: Found ${foundUrls.filter(url => url).length} URLs out of ${foundUrls.length} items`);

return [{
  json: {
    debug: {
      totalItems: inputItems.length,
      foundUrls: foundUrls.filter(url => url).length,
      urls: foundUrls,
      message: "Check console logs for detailed debug information"
    }
  }
}];

// Debug: Check trendId values and types
const items = $input.all();
console.log('🔍 Split Trends - trendId values:');
items.forEach((item, index) => {
  console.log(`Item ${index}: trendId = ${item.trendId} (type: ${typeof item.trendId})`);
});
return items;

// Add imageId to match with trendId (avoiding reserved pairedItem key)
const items = $input.all();
console.log(`�� Processing ${items.length} items`);

const processedItems = items.map((item, index) => {
  console.log(`📸 Item ${index} input:`, Object.keys(item));

  // Use imageId instead of pairedItem to avoid reserved key
  const newItem = {
    imageBinary: item.json.base64Image, // Extract from json wrapper
    imageId: index // This will match with trendId
  };

  console.log(`✅ Item ${index} output:`, Object.keys(newItem));
  return newItem;
});

console.log(`✅ Processed ${processedItems.length} items`);
return processedItems;

// Debug: Check what Extract from File actually outputs
const items = $input.all();
console.log('🔍 Extract from File - Full structure:');
items.forEach((item, index) => {
  console.log(`Item ${index}:`, JSON.stringify(item, null, 2));
  console.log(`Item ${index} keys:`, Object.keys(item));
  console.log(`Item ${index} has base64Image:`, 'base64Image' in item);
  console.log(`Item ${index} base64Image length:`, item.base64Image ? item.base64Image.length : 0);
});
return items;

// Transform data for individual API requests
