console.log(` Processing ${inputItems.length} merged items for individual API requests...`);

// Transform each item to the expected API format and return individually
const apiData = inputItems.map(item => ({
  trends: [{
    ...item.json.trend,  // Spread the trend properties directly
    imageBinary: item.json.imageBinary
  }]
}));

console.log(`✅ Created ${apiData.length} individual API payloads`);

// Return each item separately so HTTP Request processes them individually
return apiData.map(data => ({ json: data }));
