// Process Articles for Strategic Trend Analysis

const TZ = 'Europe/London';
const MIN_TITLE_LEN = 10;
const MIN_SUMMARY_LEN = 30;
const MAX_ITEMS = 100;
const LOOKBACK_DAYS = 7;

const BRAND_TAGS = ['retail','sportswear','energy drinks'];
const REGION = 'UK';
const LIMITS = { max_items: 20, min_confidence: 40 };

function stripHtml(s='') {
  return (s || '')
    .replace(/<script[\s\S]*?<\/script>/gi, '')
    .replace(/<style[\s\S]*?<\/style>/gi, '')
    .replace(/<[^>]+>/g, '')
    .replace(/\s+/g, ' ')
    .trim();
}
function hostFromUrl(u='') { try { return new URL(u).hostname.replace(/^www\./,''); } catch { return ''; } }
function toISO(d) {
  if (!d) return null;
  const tryDates = Array.isArray(d) ? d : [d];
  for (const raw of tryDates) {
    const t = raw ? Date.parse(raw) : NaN;
    if (!Number.isNaN(t)) return new Date(t).toISOString();
  }
  return null;
}
function withinLookback(iso) {
  if (!iso) return false;
  const now = new Date();
  const then = new Date(now); then.setDate(now.getDate() - LOOKBACK_DAYS);
  return new Date(iso) >= then;
}
function normTitle(s='') { return s.toLowerCase().replace(/\s+/g,' ').trim(); }

const inputItems = $input.all();

// Flatten RSS outputs
let raw = [];
for (const it of inputItems) {
  if (Array.isArray(it.json?.items)) raw = raw.concat(it.json.items);
  else raw.push(it.json);
}

// Normalize articles for trend analysis
const normalised = [];
for (const r of raw) {
  const title = (r.title || r.headline || '').trim();
  const url = (r.link || r.url || r.guid || '').trim();
  const summary = stripHtml(r.description || r.contentSnippet || r.summary || r.content || '');
  const publishedAt = toISO([r.isoDate, r.pubDate, r.published, r.updated, r.date]) || new Date().toISOString();

  // Simplified source detection for trend context
  let source = 'Market Intelligence';
  const host = hostFromUrl(url);
  if (host.includes('adweek')) source = 'Adweek';
  else if (host.includes('campaignlive')) source = 'Campaign';
  else if (host.includes('reddit.com')) source = 'Reddit';

  if (title.length < MIN_TITLE_LEN) continue;
  if (summary.length < MIN_SUMMARY_LEN) continue;
  if (!withinLookback(publishedAt)) continue;

  normalised.push({ title, summary, source, publishedAt });
}

// Dedupe by title similarity
const seenTitles = new Set(); const deduped = [];
for (const it of normalised) {
  const key = normTitle(it.title);
  if (seenTitles.has(key)) continue;
  seenTitles.add(key);
  deduped.push(it);
}

// Sort and limit
deduped.sort((a,b) => new Date(b.publishedAt) - new Date(a.publishedAt));
const items = deduped.slice(0, MAX_ITEMS);

console.log(`📊 Processing ${items.length} articles for strategic trend extraction`);
console.log(`🎯 Target: Extract ${LIMITS.max_items} strategic trends for advertising agencies`);

// Create analysis summary for agent
const articleSummary = items.map(item => `"${item.title}" - ${item.summary.substring(0, 200)}...`).join('\n\n');

return [{
  json: {
    articleSummary,
    articleCount: items.length,
    sources: [...new Set(items.map(i => i.source))],
    brand_tags: BRAND_TAGS,
    region: REGION,
    limits: LIMITS
  }
}];

// Finalize strategic trends for visualization

const data = $json;
const trends = Array.isArray(data.trends) ? data.trends : [];

// Ensure minimum trend count
if (trends.length < 10) {
  throw new Error(`Only ${trends.length} trends generated. Minimum required: 10`);
}

// Fix sourceSummary structure
if (!data.sourceSummary || !Array.isArray(data.sourceSummary.sources)) {
  data.sourceSummary = {
    totalFetched: trends.length,
    afterCluster: trends.length,
    sources: ['Strategic Market Intelligence']
  };
}

// Enhanced viz properties for strategic trends
for (let i = 0; i < trends.length; i++) {
  const trend = trends[i];

  if (trend.scores) {
    const totalScore = trend.scores.total || 50;
    const velocity = trend.scores.velocity || 50;
    const novelty = trend.scores.novelty || 50;

    // Enhanced size mapping (10-20 for strategic importance)
    trend.viz = trend.viz || {};
    trend.viz.size = Math.max(10, Math.min(20, Math.round(10 + (totalScore / 100) * 10)));

    // Enhanced intensity based on velocity + novelty
    const intensityScore = (velocity + novelty) / 2;
    trend.viz.intensity = Number((1.2 + (intensityScore / 100) * 1.8).toFixed(2));

    // Strategic category colors
    const cat = (trend.category || 'Technology').toLowerCase();
    let hue;
    switch(cat) {
      case 'technology': case 'ai': hue = 240; break; // Blue
      case 'media': case 'culture': hue = 300; break; // Purple  
      case 'retail': case 'consumer behaviour': hue = 120; break; // Green
      case 'creativity': hue = 60; break; // Yellow
      case 'regulation': case 'data & privacy': hue = 0; break; // Red
      case 'sustainability': hue = 150; break; // Teal
      default: hue = 210; // Default blue
    }
    trend.viz.colorHint = `hsl(${hue}, 80%, 50%)`;

    // Add unique trend ID if missing
    if (!trend.id) {
      trend.id = `trend_${i + 1}_${Date.now().toString().slice(-4)}`;
    }
  }
}

data.generatedAt = data.generatedAt || new Date().toISOString();

// Strategic logging
console.log(`🎯 Generated ${trends.length} strategic trends for agency intelligence`);
const scoreRange = trends.map(t => t.scores?.total || 0);
const minScore = Math.min(...scoreRange);
const maxScore = Math.max(...scoreRange);
console.log(`📊 Strategic Impact Range: ${minScore}-${maxScore}`);
console.log(`🔍 Categories: ${[...new Set(trends.map(t => t.category))].join(', ')}`);

trends.slice(0, 5).forEach((trend, i) => {
  console.log(`${i+1}. "${trend.title}" (${trend.category}) - Impact: ${trend.scores?.total}`);
});

return [{ json: data }];

// Generate detailed image prompts for each trend using the template from imageprompt.md
// This node should be inserted between "Finalize Trends" and the OpenAI image generation node

const inputData = $json;
const trends = Array.isArray(inputData.trends) ? inputData.trends : [];

console.log(`🎨 Generating image prompts for ${trends.length} trends...`);

// Process each trend to create detailed image prompts
const processedTrends = trends.map((trend, index) => {
  const { title, category, creative } = trend;

  // Use the existing imagePrompt as the base, or create one from the title
  const basePrompt = creative?.imagePrompt || `A 3D visualization representing "${title}"`;

  // Create the detailed prompt using the template from imageprompt.md
  const detailedPrompt = `Create a hyper-detailed 3D render of ${basePrompt} symbolizing "${title}" (trend title) in the ${category} (trend category) category.

  • Perspective: Slightly angled from above, showing depth and dimension.
  • Primary color tone: Use colors that complement the ${category} theme prominently in lighting, accents, and key elements.
  • Details: Include realistic textures, materials, and lighting that represent the trend concept.
  • Symbolism: Incorporate visual metaphors that clearly represent "${title}" and its significance in ${category}.
  • Lighting: Studio-quality lighting with soft shadows and realistic reflections.
  • Style: Realistic yet slightly stylized for visual clarity, cinematic depth of field.
  • Background: Transparent PNG with alpha channel (no sky or scenery).
  • Format: 1024x1024 PNG with transparent background.`;

  console.log(`📝 Generated prompt for "${title}": ${detailedPrompt.substring(0, 100)}...`);

  return {
    ...trend,
    imageGenerationPrompt: detailedPrompt
  };
});

console.log(`✅ Generated ${processedTrends.length} image prompts`);

return [{
  json: {
    ...inputData,
    trends: processedTrends
  }
}];

// Split trends into individual items for image generation
// This node creates separate items for each trend so the OpenAI node can process them individually

const inputData = $json;
const trends = Array.isArray(inputData.trends) ? inputData.trends : [];

console.log(`🔄 Splitting ${trends.length} trends into individual items for image generation...`);

// Limit to first 5 trends for testing (to control costs and processing time)
// In production, you can increase this number or process all trends
const MAX_TRENDS_FOR_IMAGES = 5;
const trendsToProcess = trends.slice(0, MAX_TRENDS_FOR_IMAGES);
console.log(`📊 Processing first ${trendsToProcess.length} trends for image generation (out of ${trends.length} total)`);

// Create separate items for each trend to process
const individualTrends = trendsToProcess.map(trend => ({
  json: {
    trend: trend,
    imageGenerationPrompt: trend.imageGenerationPrompt,
    trendId: trend.id,
    originalTrendIndex: trends.indexOf(trend)
  }
}));

console.log(`✅ Created ${individualTrends.length} individual trend items for image generation`);

return individualTrends;

// Process individual image generation results and merge back with original trends
// This node processes the results from the OpenAI image generation node and merges them back

const inputItems = $input.all();

console.log(`🖼️ Processing ${inputItems.length} image generation results...`);

// Create a map of trend IDs to image URLs
const imageResults = new Map();

inputItems.forEach(item => {
  const trendId = item.json.trendId;
  const imageUrl = item.json.url || null; // OpenAI returns the image URL in the 'url' field
  const trendTitle = item.json.trend.title;

  if (imageUrl) {
    console.log(`✅ Image generated for "${trendTitle}": ${imageUrl}`);
    imageResults.set(trendId, imageUrl);
  } else {
    console.log(`❌ No image generated for "${trendTitle}"`);
    imageResults.set(trendId, null);
  }
});

// Count successful generations
const successfulGenerations = Array.from(imageResults.values()).filter(url => url !== null).length;
console.log(`🎯 Image processing complete: ${successfulGenerations}/${inputItems.length} successful`);

// Get the original trends data from the previous node
// We need to reconstruct the full trends array with image URLs added
const originalTrends = [];

// This is a simplified approach - in a real implementation, you might want to
// store the original trends data in a more accessible way
// For now, we'll create a basic structure with the processed trends

const processedTrends = inputItems.map(item => {
  const trend = item.json.trend;
  const imageUrl = imageResults.get(trend.id);

  return {
    ...trend,
    creative: {
      ...trend.creative,
      imageUrl: imageUrl
    }
  };
});

console.log(`📊 Final trends with images: ${processedTrends.length} trends processed`);

return [{
  json: {
    trends: processedTrends,
    generatedAt: new Date().toISOString(),
    sourceSummary: {
      totalFetched: processedTrends.length,
      afterCluster: processedTrends.length,
      sources: ['Strategic Market Intelligence']
    }
  }
}];
